def clear_screen():
    os.system('cls' if os.name == 'nt' else 'clear')
def show_banner():
    clear_screen()
    print(LOGO)
    print(BANNER)
    print(f'\n{TITLE.center(80)}')
    print(LINE)
def pause():
    input('\nTekan Enter untuk kembali ke menu utama...')
def save_text(category, lang, text):
    folder = 'hasil_teks'
    os.makedirs(folder, exist_ok=True)
    filename = f'{folder}/{category}_{lang}.txt'
    with open(filename, 'w', encoding='utf-8') as f:
        f.write(text)
    print(f'\nâœ… File berhasil disimpan ke: {filename}')
def tampilkan_teks(category):
    clear_screen()
    show_banner()
    print(f'\nğŸ“œ Pilih Bahasa untuk Teks Banding: ({category.upper()})')
    print('[1] Indonesia\n[2] English\n[3] Mexico (EspaÃ±ol)\n[4] Arabic (Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©)\n[5] Brazil (PortuguÃªs)\n[6] Kembali')
    choice = input('\nPilih opsi: ')
    languages = ['Indonesia', 'English', 'Mexico', 'Arabic', 'Brazil']
    if choice in ['1', '2', '3', '4', '5']:
        lang = languages[int(choice) - 1]
        clear_screen()
        show_banner()
        print(f'ğŸ“„ Teks Banding WhatsApp ({category.upper()}) â€” {lang}\n{LINE}')
